mapper.select:
SELECT S.SB_NO, S.SB_SALE_DATE FROM TBL_QNA Q JOIN TBL_SALED_BOOK S ON Q.M_ID = S.M_ID 
WHERE S.M_ID = #{m_id} 
ORDER BY S.SB_SALE_DATE DESC

mapper.update:
UPDATE TBL_QNA 
SET A_TEXT=#{a_text}, A_REG_DATE=SYSTIMESTAMP, A_MOD_DATE=SYSTIMESTAMP 
WHERE Q_NO=#{q_no}

UPDATE TBL_REVIEW SET R_TEXT = #{r_text}, R_STARS =
#{r_stars}, R_MOD_DATE =
SYSTIMESTAMP WHERE R_NO = #{r_no}
